td.script:
function checkUserOpinion(field) {
  var l = field.value.trim().length;
  var userAlert = MyShopsBar.elems.userAlert;
  if(l > 15 && l < 40){
    MyShopsBar.elems.userAlert.style.display = '';
  } else if(l <= 15 || l >= 30){
    MyShopsBar.elems.userAlert.style.display = 'none';
  }
}

form.script:
var links =  $('*[id ^= "rating-stars"] .b-rating__star').css('cursor', 'pointer');
links.hover(
    function(){
      $(this).prevAll().andSelf().addClass('b-rating__star-my-hover');
      $(this).nextAll().addClass('b-rating__star-hover');
    },
    function(){
      links.removeClass('b-rating__star-hover b-rating__star-my-hover');
    }
);

